mapper.select:
	  select DATE_FORMAT(startdate, '%Y') AS YEAR, DATE_FORMAT(startdate, '%m') AS MONTH, STATUS, 
      COUNT(*) AS sleepoverCount
      FROM SLEEPOVERHISTORY
      WHERE DATE_FORMAT(startdate, '%Y')=#{year} AND UNIVERSITYCODE=#{universityCode} AND DOMITORYNAME = #{domitoryName} 
      GROUP BY MONTH, STATUS 
      ORDER BY MONTH;
