mapper.select:
		select DATE_FORMAT(startdate, '%Y') AS YEAR, DATE_FORMAT(startdate, '%m') AS MONTH,
      COUNT(*) AS sleepoverCount
      FROM SLEEPOVERHISTORY
      WHERE `STATUS`=11 AND DATE_FORMAT(startdate, '%Y')=#{param1} AND UNIVERSITYCODE=${parma2} AND DOMITORYNAME = #{param3} 
      GROUP BY month
      ORDER BY MONTH;
