mapper.select:
SELECT FILENUM, IDX, FILENAME, FILEREALNAME, FILESIZE FROM
FILE WHERE IDX
= #{idx}

mapper.update:
UPDATE POST
SET TITLE =
#{title}, CONTENT= #{content}, STATUS=#{status}
,WRITEDATE=NOW()
WHERE
IDX = #{idx};

mapper.select:
SELECT COUNT(*)
FROM POSTLIKE
WHERE IDX = #{param1} AND MEMBERID = #{param2}
