mapper.select:
SELECT SLEEPOVERIDX, MEMBERID, UNIVERSITYCODE, STARTDATE, ENDDATE, STATUS, SLEEPOVERREASON, SLEEPOVERCONFIRM
FROM SLEEPOVERHISTORY
WHERE UNIVERSITYCODE=#{universitycode}

SELECT SLEEPOVERIDX, MEMBERID, UNIVERSITYCODE, STARTDATE, ENDDATE, STATUS, SLEEPOVERREASON, SLEEPOVERCONFIRM from SLEEPOVERHISTORY WHERE universitycode=#{universitycode}
AND MEMBERID = #{memberid}
AND ((DATE(startdate) <= STR_TO_DATE(#{startdate},'%Y-%m-%d') AND DATE(enddate) >= STR_TO_DATE(#{startdate},'%Y-%m-%d')) OR (DATE(startdate) <= STR_TO_DATE(#{enddate},'%Y-%m-%d') AND DATE(enddate)>= STR_TO_DATE(#{enddate},'%Y-%m-%d')))
